import React from 'react';
import { NavLink, useNavigate } from 'react-router-dom';

import PropTypes from 'prop-types';
import styles from './Header.module.css';
import Logo from '../../images/Logo.png';

function Header({ user = null, setUser }) {
  const navigate = useNavigate();

  const disconnect = () => {
    localStorage.clear();
    setUser(null);
    navigate('/');
  };

  return (
    <header className={styles.Header}>
      <div className="container">
        <img src={Logo} alt="logo mon vieux grimoire" />
        <ul>
          <li>
            <NavLink to="/" end className={({ isActive }) => (isActive ? styles.activeLink : undefined)}>
              Accueil
            </NavLink>
          </li>
          <li>
            <NavLink to="/Ajouter" className={({ isActive }) => (isActive ? styles.activeLink : undefined)}>
              Ajouter un livre
            </NavLink>
          </li>
          <li>
            {!user ? (
              <NavLink to="/Connexion" className={({ isActive }) => (isActive ? styles.activeLink : undefined)}>
                Se connecter
              </NavLink>
            ) : (
              <span tabIndex={0} role="button" onKeyUp={disconnect} onClick={disconnect}>
                Se d√©connecter
              </span>
            )}
          </li>
        </ul>
      </div>
    </header>
  );
}

Header.propTypes = {
  user: PropTypes.shape({
    userId: PropTypes.string,
    token: PropTypes.string,
  }),
  setUser: PropTypes.func.isRequired,
};

export default Header;
